rule "ThreatIntellOTX:Inbound - src"
when
  ( has_field("src_addr_threat_indicated") &&
to_string($message."src_addr_threat_indicated") == "true"
)

then
 let intel = otx_lookup_ip(to_string($message.src));
 set_field("otx_threat_indicated", intel.otx_threat_indicated);
 set_field("otx_threat_ids", intel.otx_threat_ids);
 set_field("otx_threat_names", intel.otx_threat_names);

 let whois_intel = whois_lookup_ip(to_string($message.src), "src_addr");
   set_fields(whois_intel);

end

rule "GLR-Threat_Intel_Lookup_src_address"

when 
 ( has_field("src-dst-direction") &&
   lowercase(to_string($message."src-dst-direction")) == lowercase("inbound")
 )

then

 let src_addr_intel = threat_intel_lookup_ip(to_string($message.src), "src_addr");
   set_fields(src_addr_intel);

end

rule "Inbound-allowed-tagwhois"
when
  ( has_field("src-dst-direction") &&
lowercase(to_string($message."src-dst-direction")) == lowercase("inbound")
)

  AND
  ( has_field("action") &&
lowercase(to_string($message."action")) == lowercase("allow")
)

then

 let whois_intel = whois_lookup_ip(to_string($message.src), "src_addr");
   set_fields(whois_intel);

end
